module Quest_10_input where
import Parser
import Control.Applicative (some, (<|>))
import Data.List (nub)
import Data.Char
import Data.Either

symbols = oneOf (['*', '.', '?'] ++ ['A'..'Z'])

term :: Parser Char Int String
term = filter (\c -> c /= '.' && c /= '*') <$> some symbols

sep1 = char '|'
sep2 = char ':'
sep3 = char '#'

parser1 :: Parser Char Int [String]
-- parser1 = (exact 2 (term <* sep1)) <:> (exact 4 (term <* sep1)) <:> (term <* sep1 <:> term <:> (pure [])) <:> (pure [])
parser1 = sepBy term sep1

run1 input = fst . fromRight ([], []) $ runParser parser1 input

parser2 :: Parser Char Int [[String]]
parser2 = sepBy (sepBy term sep2) sep1

parser3 = sepBy (blockFix parser2) sep3

blockFix :: Parser Char Int [[String]] -> Parser Char Int [[String]]
blockFix parser = foldl (zipWith (\acc x -> acc ++ [x])) [ x | x <- repeat []] <$> parser

lineWithInter :: Parser Char Int [String]
lineWithInter = some (f <$> (uncurry (++)) <$> (exact 6 symbols <~> lookahead (exact 2 symbols))) <* (exact 2 symbols)
  where
    f = filter (\c -> c /= '.' && c /= '*')

blockLine = (uncurry (++)) <$> (exact 6 line <~> lookahead (exact 2 line))
  where
    line = lineWithInter <* sep1

blocks :: Parser Char Int [[[String]]]
blocks = some . blockFix $ blockLine

run2 input = fst . fromRight ([], []) $ runParser parser3 input
run3 input = fst . fromRight ([], []) $ runParser blocks input

